#coding:utf-8

"""
ID:          issue-7434
ISSUE:       https://github.com/FirebirdSQL/firebird/issues/7434
TITLE:       Crash or error in SUBSTRING()'s regexp parser with UTF-8 encoding
NOTES:
    [26.04.2022] pzotov
    Confirmed bug on 3.0.11.33648
    Checked on 3.0.11.33650 -- all fine.
"""

import pytest
from firebird.qa import *

db = db_factory(charset = 'utf8')

test_script = """
    set list on;
    select substring('123' similar '%#"#"%' escape '#') from rdb$database;
"""

act = isql_act('db', test_script)

expected_stdout = """
    SUBSTRING                       <null>
"""

@pytest.mark.version('>=3.0.11')
def test_1(act: Action):
    act.expected_stdout = expected_stdout
    act.execute(combine_output = True)
    assert act.clean_stdout == act.clean_expected_stdout
