#coding:utf-8

"""
ID:          decfloat.ddl-indices
TITLE:       Check ability to create table with 16 fields of type decfloat(34), plus create indices and add data
ISSUE:       5803
JIRA:        CORE-5535
DESCRIPTION:
  See  doc/sql.extensions/README.data_types
"""

import pytest
from firebird.qa import *

db = db_factory()

test_script = """
    set bail on;
    set list on;
    recreate table test(
       f1 decfloat(34)
    default 
      ,f2 decfloat(34)
    default -
      ,f3 decfloat(34)
    default 
      ,f4 decfloat(34)
    default -
      ,f5 decfloat(34)
    default 
      ,f6 decfloat(34)
    default -
      ,f7 decfloat(34)
    default 
      ,f8 decfloat(34)
    default -
      ,f9 decfloat(34)
    default 
      ,f10 decfloat(34)
    default -
      ,f11 decfloat(34)
    default 
      ,f12 decfloat(34)
    default -
      ,f13 decfloat(34)
    default 
      ,f14 decfloat(34)
    default -
      ,f15 decfloat(34)
    default 
      ,f16 decfloat(34)
    default -
    );
    alter table test add fff computed by (
       f1
      + f2
      + f3
      + f4
      + f5
      + f6
      + f7
      + f8
      + f9
      + f10
      + f11
      + f12
      + f13
      + f14
      + f15
      + f16
    );
    create index test_compound on test(
       f1
      ,f2
      ,f3
      ,f4
      ,f5
      ,f6
      ,f7
      ,f8
      ,f9
      ,f10
      ,f11
      ,f12
      ,f13
      ,f14
      ,f15
      ,f16
    );
    create index test_computed1 on test computed by (
       f1
      + f2
      + f3
      + f4
      + f5
      + f6
      + f7
      + f8
      + f9
      + f10
      + f11
      + f12
      + f13
      + f14
      + f15
      + f16
    );
    create index test_computed2 on test computed by (fff);
    commit;

    insert into test default values;
    select * from test where fff > 0;
"""

act = isql_act('db', test_script)

expected_stdout = """
    F1                               1.239999999999999999999955600000000E+1023
    F2                              -1.239999999999999999999955600000000E+1022
    F3                               1.239999999999999999999955600000000E+1022
    F4                              -1.239999999999999999999955600000000E+1022
    F5                               1.239999999999999999999955600000000E+1022
    F6                              -1.239999999999999999999955600000000E+1022
    F7                               1.239999999999999999999955600000000E+1022
    F8                              -1.239999999999999999999955600000000E+1022
    F9                               1.239999999999999999999955600000000E+1022
    F10                             -1.239999999999999999999955600000000E+1022
    F11                              1.239999999999999999999955600000000E+1022
    F12                             -1.239999999999999999999955600000000E+1022
    F13                              1.239999999999999999999955600000000E+1022
    F14                             -1.239999999999999999999955600000000E+1022
    F15                              1.239999999999999999999955600000000E+1022
    F16                             -1.239999999999999999999955600000000E+1022
    FFF                              1.115999999999999999999960040000000E+1023
"""

@pytest.mark.version('>=4.0')
def test_1(act: Action):
    act.expected_stdout = expected_stdout
    act.execute()
    assert act.clean_stdout == act.clean_expected_stdout
